import Phaser from "phaser";
import mapaPNG  from "./assets/assetsmap.png";
import mapaJSON from "./assets/map.json";

const config = {
    type: Phaser.AUTO,
    parent: "phaser-example",
    width: 650,
    height: 650,
    scene: {
        preload: preload,
        create: create,
        update: update
    }
};

const game = new Phaser.game(config);

function preload(){
    this.load.image("tiles",mapaPNG);
    this.load.tilemapTiledJSON("mapa", mapaJSON);
}

function create(){
    const mapa = this.make.tilemap({key: "mapa"});
    const tileset = mapa.addTilesetImagem("assets","tiles");

    const ground = mapa.createStaticLayer("mapa", tileset, 0, 0);
}

function update(){}

