class Menu extends Phaser.Scene{
    constructor(){
        super("Menu");
    }

    preload(){
        this.load.image("btnPlay", "img/botao.png");
        this.load.image("fundo", "img/fundo.png");
        this.load.image("logo", "img/logo.png");
    }

    create(){
        this.add.image(0, -20, "fundo").setOrigin(0, 0);
        let btnPlay = this.add.image(310, 400, "btnPlay").setOrigin(0, 0);
        btnPlay.setInteractive();

        this.add.image(-10, 40, "logo").setOrigin(0, 0);


        btnPlay.on("pointerdown", ()=> this.scene.start("Game"));
    }
}