class MenuFinal extends Phaser.Scene{
    constructor(){
        super("MenuFinal");
    }

    preload(){
        this.load.image("fundo", "img/fundo.png");
        this.load.image("btnReturn", "img/botao-retornar.png");
        
    }

    create(){
        this.add.image(0, -20, "fundo").setOrigin(0, 0);
        this.add.text("GameOver");
        let btnReturn = this.add.image(310, 400, "btnReturn").setOrigin(0, 0);
        btnReturn.setInteractive();

        btnReturn.on("pointerdown", ()=> this.scene.start("Menu"));
    }
}